import { Inter } from "next/font/google";
import "./globals.css";
import Providers from "./providers";
import { getAuthenticatedAppForUser } from "@/lib/firebase/serverApp";
import { Metadata } from "next";

const inter = Inter({ subsets: ["latin"] });

