export default async function RootLayout({
  children,
}: Readonly<{
  children: React.ReactNode;
}>) {
  const doc = await getAuthenticatedAppForUser();

  return (
    <html lang="en">
      <body>
        <Providers currentUser={doc.currentUser}>{children}</Providers>
      </body>
    </html>
  );
}
